import React, { useEffect, useState } from 'react';
import mqtt from 'mqtt';

const App = () => {
  const [data, setData] = useState('');
  const [mode, setMode] = useState('pulse');

  useEffect(() => {
    const options = {
      protocol: 'wss',
      port: 8884,

    };
    const client = mqtt.connect('wss://9165a2be0b2244a2be418b2d702267d5.s1.eu.hivemq.cloud:8884/mqtt', options);

    client.on('connect', () => {
      console.log('Connected to MQTT broker');
      client.subscribe('Daten');
      client.subscribe('Moduswechsel');
    });

    client.on('message', (topic, message) => {
      const payload = message.toString();
      if (topic === 'Daten') {
        setData(payload);
      } else if (topic === 'Moduswechsel') {
        setMode(payload === '1' ? 'raw' : 'pulse');
      }
    });

    return () => {
      client.end();
    };
  }, []);

  const publishModeChange = (mode) => {
    const options = {
      protocol: 'wss',
      port: 8884,

    };
    const client = mqtt.connect('wss://9165a2be0b2244a2be418b2d702267d5.s1.eu.hivemq.cloud:8884/mqtt', options);
    client.on('connect', () => {
      client.publish('Moduswechsel', mode);
      client.end();
    });
  };

  return (
    <div>
      <h1>MQTT Web App</h1>
      <button onClick={() => publishModeChange('1')}>Switch to Raw Data Mode</button>
      <button onClick={() => publishModeChange('0')}>Switch to Pulse Data Mode</button>

      <div id="dataDisplay">
        <h2>Data:</h2>
        <div id="dataOutput">
          {mode === 'pulse' ? (
            <div>
              <p>Heart Rate: {data.split(',')[0]} bpm</p>
              <p>Blood Oxygen: {data.split(',')[1]}%</p>
            </div>
          ) : (
            <p>Raw Data: {data}</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
